import React from "react";
import { Button, Card, Col } from "react-bootstrap";
import { Link } from "react-router-dom";
import { useState, useEffect } from "react";
import { UserProvider } from "../Provider/UserProvider";



export default function UsersPage() {

    const [usersn, SetUsers] = useState([])

    const userProvider = new UserProvider()



    useEffect(() => {
        let datas = userProvider.getUsers()
        SetUsers(datas)
    }, [])


    function remove(user) {
        let rep = window.confirm(
            `voulez vous vraiment supprimer l'utilisateur ?? ${user.prenom} ${user.nom}`
        )
        if (rep) {
            userProvider.remove(user)
            let datas = userProvider.getUsers()
            SetUsers(datas)
        }
    }


    let displayUsers = users.map((user, indice) => {
        return(
            <>
                <Col lg={5} md={5}>
                    <Card>
                        <Card.body>
                            <Card.title>
                                {user.prenom} {user.nom}
                            </Card.title>
                        </Card.body>
                        <Card.body>
                            <Button className="button-del" as = {Link} to={"/users/" + user.id} variant="Warning">Modifier<p></p></Button>
                            <Button className="button-del" onClick={()=> remove(user)}>Supprimer </Button>
                        </Card.body>
                    </Card>
                </Col>
            </>
        )
    })























    return (
        <>

            <h1>
                Liste des Utilisateurs
            </h1>

            <br />
            <Button variant="outline-secondary" as={Link} to="/add">Cr√©er</Button>
        </>
    )
}
