import React from "react";
import {Button, Col, Card, Container} from 'react-bootstrap';
import {Link} from 'react-router-dom';
import { useState, useEffect } from "react";
import { UserProvider } from "../Providers/UserProvider";

export default function UsersPage(){
    const [users, setUsers] = useState([])
    const userProvider = new UserProvider()

  // UseEffect où on récupère le contenu du la bdd spa-users dans un usestate (user)

useEffect(() => {
    //on utilise une fonction du provider qui récupère les élements du localstorage

    let datas = userProvider.getUsers()

    // on le stock dans le usestate users, à l'aide de setUsers

    setUsers(datas)
  }, [])
    


 function remove(user) {
    let rep = window.confirm(
      `Etes-vous sur de vouloir supprimer  ${user.prenom} ${user.nom}`
    )
    if (rep) {
      userProvider.remove(user)
      let datas = userProvider.getUsers()
      setUsers(datas)
    }
  }

  let displayUsers = users.map((user, indice) => {

    return(
    <>  
        <Col lg={5} md={5}>
         <Card>
            <Card.Body>
              <Card.Title>
                {user.prenom} {user.nom}
              </Card.Title>
            </Card.Body>
            <Card.Body>
              <Button className="button-del" as={Link} to={'/users/' + user.id} variant="warning">
                Modifier
              </Button>
              <p></p>
              <Button className="button-del" onClick={() => remove(user)}>
                Supprimer 
              </Button>
            </Card.Body>
          </Card>
        </Col>

    </>
    )
})
return(
    <>
    <h1>
            Liste des utilisateurs
    </h1>

        <br />
        <br />  
        <Button variant="outline-secondary" as={Link} to="/add">Créer</Button>

        <hr />
    <br></br>
      <Container  key={users.id} className='Container' >{displayUsers}</Container>
    </>
)

}
