import React, { Component } from "react";

export default function Add() {
    
    class Add extends Component {
        constructor(props) {
            super(props)
            this.state = {
                number1: 0,
                number2: 0,
                result: 0
            }
        }


        calculate = () => {
            this.setState({
                result: parseInt(this.state.number1, 10) + parseInt(this.state.number2, 10)
            })
        }

        render() {
            return (

                <>
                    <div class="col-md-5 col-md-offset-5">
                        <h2>Ajouter Deux Nombres</h2>
                        <form id="create-user-form">
                            <div className="number-inputs">
                                <div style={{ width: '35%' }} class="form-group">
                                    <input type='number' placeholder="Premier Nombre" class="form-control" onChange={(e) => this.setState({ number1: e.target.value })} />
                                </div>

                                <div style={{ width: '35%' }} class="form-group">
                                    <input type='number' placeholder="Second Nombre" class="form-control" onChange={(e) => this.setState({ number2: e.target.value })} />
                                </div>
                                <input type="button" value="add" class="btn btn-success" onClick={this.Calculate} />

                            </div>
                        </form>
                        <h2>{this.state.result}</h2>
                    </div>
                </>

            );
        }



    }
}
