import React from "react";
import { useNavigate, useParams } from 'react-router-dom'
import { useState, useEffect } from 'react'
import { UserProvider } from '../Provider/UserProvider';
import {Container, Col, Row, Form, Button} from 'react-bootstrap';

export default function UsersUpdate(){

    const [user, SetUsers] = useState({})
    const [formUpdate, setFormUpdate] = useState({
    id: '',
    prenom: '',
    nom: '',
  })
const userProvider = new UserProvider()


const {id} = useParams()
const navigate = useNavigate()
useEffect(() => {
    let tmpUser = userProvider.getUserById(id)

    if (!tmpUser) {
      alert('User non trouvé dans la base')
      navigate('/users')
    } else {
      SetUsers(tmpUser)
      setFormUpdate(tmpUser)
    }
  }, [id, navigate])

  function update(e) {
    e.preventDefault()
    let res = userProvider.update(formUpdate)
    if (res) navigate('/users')
    else alert("Erreur lors de l'enregistrement")
  }

  function reset() {
    setFormUpdate(user)
  }
  return(
    <>
    <Container>
        <Row>
            <Col>
                <h1>Modifier un membre de la marine</h1>
                <hr />
            </Col>
        </Row>
    </Container>

    <Row>
      <Col md={6}>
        <Form onSubmit={e => update(e)}>
          <Form.Group className="mb-3">
            <Form.Label className="title-modif">Prénom</Form.Label>
            <Form.Control
              type="text"
              placeholder="Modifier le prenom"
              value={formUpdate.prenom}
              onChange={e => {
                let tmp = { ...formUpdate }
                tmp.prenom = e.target.value
                setFormUpdate(tmp)
              }}
              required
            />
          </Form.Group>


              <Form.Group className="mb-3">
            <Form.Label className="title-modif">Nom</Form.Label>
            <Form.Control
              type="text"
              placeholder="Modifier le nom"
              value={formUpdate.nom}
              onChange={e => {
                let tmp = { ...formUpdate }
                tmp.nom = e.target.value
                setFormUpdate(tmp)
              }}
              required
            />
          </Form.Group>

              <hr />

          <Button type="submit" className="button-up">
            Enregistrer
          </Button>
          <p></p>
          <Button
            variant="outline-secondary"
            className="button-up"
            type="reset"
            onClick={reset}
          >
            Annuler
          </Button>
            </Form>
        </Col>
    </Row>
    </>

)
}
